mapper.update:
update orderItem
set pid = #{pid,jdbcType=INTEGER},
  oid = #{oid,jdbcType=INTEGER},
  uid = #{uid,jdbcType=INTEGER},
  number = #{number,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, pid, oid, uid, number
from orderItem
where id = #{id,jdbcType=INTEGER}
