mapper.update:
update review
set pid = #{pid,jdbcType=INTEGER},
  uid = #{uid,jdbcType=INTEGER},
  content = #{content,jdbcType=VARCHAR},
  createDate = #{createDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, pid, uid, content, createDate
from review
where id = #{id,jdbcType=INTEGER}
